descriptor.controllerScript:
let $scope;
let settings;
let attributeService;
let toast;
let utils;
let types;
let $translate;
let map;
let mapReverse;

self.onInit = function() {
    $scope = self.ctx.$scope;
    attributeService = $scope.$injector.get('attributeService');
    toast = $scope.$injector.get('toast');
    utils = $scope.$injector.get('utils');
    types = $scope.$injector.get('types');
    $translate = $scope.$injector.get('$translate');
    settings = angular.copy(self.ctx.settings) || {};
    $scope.settings = settings;
    $scope.isValidParameter = true;
    $scope.dataKeyDetected = false;

    settings.trueValue = utils.customTranslation(settings.trueValue, settings.trueValue) || true;
    settings.falseValue = utils.customTranslation(settings.falseValue, settings.falseValue) || false;

    map = {"true":settings.trueValue, "false": settings.falseValue};
    mapReverse = {[settings.trueValue]:true, [settings.falseValue]:false};
    $scope.checkboxValue = "false";
    $scope.currentValue = map[$scope.checkboxValue];

    $scope.changed = function () {
        $scope.currentValue = map[$scope.checkboxValue];
        $scope.updateAttribute();
    }

    if (self.ctx.datasources && self.ctx.datasources.length) {
        var datasource = self.ctx.datasources[0];
        if (datasource.type === types.datasourceType.entity) {
            if (datasource.entityType && datasource.entityId) {
                $scope.entityName = datasource.entityName;
                if (settings.widgetTitle && settings.widgetTitle.length) {
                    $scope.titleTemplate = utils.customTranslation(settings.widgetTitle, settings.widgetTitle);
                } else {
                    $scope.titleTemplate = self.ctx.widgetConfig.title;
                }

                $scope.entityDetected = true;
            }
        }
        if (datasource.dataKeys.length) {
            if (datasource.dataKeys[0].type != types.dataKeyType.attribute) {
                $scope.isValidParameter = false;
            } else {
                $scope.currentKey = datasource.dataKeys[0].name;
                $scope.dataKeyType = datasource.dataKeys[0].type;
                $scope.dataKeyDetected = true;
            }
        }
    }

    self.ctx.widgetTitle = utils.createLabelFromDatasource(self.ctx.datasources[0], $scope.titleTemplate);

    $scope.updateAttribute = function () {
        if ($scope.entityDetected) {
            var datasource = self.ctx.datasources[0];

            attributeService.saveEntityAttributes(
                datasource.entityType,
                datasource.entityId,
                types.attributesScope.server.value,
                [
                    {
                        key: $scope.currentKey,
                        value: mapReverse[$scope.currentValue] || false
                    }
                ]
            ).then(
                function success() {
                    $scope.originalValue = $scope.currentValue;
                    if (settings.showResultMessage) {
                        toast.showSuccess($translate.instant('widgets.input-widgets.update-successful'), 1000, angular.element(self.ctx.$container), 'bottom left');
                    }
                },
                function fail() {
                    if (settings.showResultMessage) {
                        toast.showError($translate.instant('widgets.input-widgets.update-failed'), angular.element(self.ctx.$container), 'bottom left');
                    }
                }
            );
        }
    };
}

self.onDataUpdated = function() {
    try {
        if ($scope.dataKeyDetected) {
            $scope.checkboxValue = ($scope.originalValue = self.ctx.data[0].data[0][1]) || false;
            $scope.currentValue = map[$scope.checkboxValue];
            $scope.$digest();
        }
    } catch (e) {
        console.log(e);
    }
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1
    }
}

self.onResize = function() {}
